import React, { Component } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import $ from 'jquery';
import Card from './Card';

class Bucket extends Component {
    constructor() {
        super()
        this.state = {
            Bucket: [
                {
                    nama: "Doritos", deskripsi: "Kemasan dengan berat 650g", harga: 6000, jumlah: 2,

                    foto: "data:image/jpeg;base64,"
                },
                {
                    nama: "Indomie Goreng", deskripsi: "", harga: 3000, jumlah: 4,

                    foto: "data:image/jpeg;base64,"
                },
                {
                    nama: "Teh Pucuk", deskripsi: "Kemasan botol ukuran 1 liter", harga: 9000, jumlah: 2,

                    foto: "data:image/jpeg;base64,"
                },
                {
                    nama: "Gulaku", deskripsi: "", harga: 7500, jumlah: 2,

                    foto: "data:image/jpeg;base64,"
                },
            ],
            action: "",
            nama: "",
            deskripsi: "",
            harga: "",
            jumlah: "",
            foto: "",
            selectedItem: null,
        }
        this.state.filterBucket = this.state.Bucket
    }
    render() {
        return (
            <div className="container">
                <div className="row">
                    {this.state.filterBucket.map((item, index) => (
                        <Card
                            nama={item.nama}
                            deskripsi={item.deskripsi}
                            harga={item.harga}
                            jumlah={item.jumlah}
                            foto={item.foto}
                            onEdit={() => this.Edit(item)}
                            onDrop={() => this.Drop(item)}
                        />
                    ))}
                </div>

                <br/>
                <button className="btn btn-primary" onClick={() => this.Add()}>
                    Tambah Data
                </button>

                {/* component modal sbg control manipulasi data */}
                <div className="modal" id="modal_Bucket">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            {/* modal header */}
                            <div className="modal-header">
                                Form Bucket
                            </div>
                            {/* modal body */}
                            <div className="modal-body">
                                <form onSubmit={ev => this.Save(ev)}>
                                    Nama Produk
                                    <input type="text" className="form-control mb-2"

                                        value={this.state.nama}
                                        onChange={ev => this.setState({
                                            nama:
                                                ev.target.value
                                        })}
                                        required />

                                    Deskripsi
                                    <input type="text" className="form-control mb-2"

                                        value={this.state.deskripsi}
                                        onChange={ev => this.setState({
                                            deskripsi:
                                                ev.target.value
                                        })}
                                        required />

                                    Harga Produk
                                    <input type="text" className="form-control mb-2"

                                        value={this.state.harga}
                                        onChange={ev => this.setState({
                                            harga
                                                : ev.target.value
                                        })}
                                        required />

                                    Jumlah yang Dibeli
                                    <input type="number" className="form-control mb-2"

                                        value={this.state.jumlah}
                                        onChange={ev => this.setState({
                                            jumlah:
                                                ev.target.value
                                        })}
                                        required />

                                    Foto
                                    <input type="url" className="form-control mb-2"

                                        value={this.state.foto}
                                        onChange={ev => this.setState({
                                            foto:
                                                ev.target.value
                                        })}
                                        required />

                                    <button className="btn btn-primary btn-block" type="submit">
                                        Simpan
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
    Add = () => {
        // menampilkan komponen modal
        $("#modal_Bucket").show()
        this.setState({
            nama: "",
            deskripsi: "",
            harga: "",
            jumlah: "",
            foto: "",
            action: "insert"
        })
    }
    Edit = (item) => {
        // menampilkan komponen modal
        $("#modal_Bucket").show()
        this.setState({
            nama: item.nama,
            deskripsi: item.deskripsi,
            harga: item.harga,
            jumlah: item.jumlah,
            foto: item.foto,
            action: "update",
            selectedItem: item
        })
    }
    Save = (event) => {
        event.preventDefault();
        // menampung data state Bucket
        let tempBucket = this.state.Bucket
        if (this.state.action === "insert") {
            // menambah data baru
            tempBucket.push({
                nama: this.state.nama,
                deskripsi: this.state.deskripsi,
                harga: this.state.harga,
                jumlah: this.state.jumlah,
                foto: this.state.foto,
            })

        } else if (this.state.action === "update") {
            // menyimpan perubahan data
            let index = tempBucket.indexOf(this.state.selectedItem)
            tempBucket[index].nama = this.state.nama
            tempBucket[index].deskripsi = this.state.deskripsi
            tempBucket[index].harga = this.state.harga
            tempBucket[index].jumlah = this.state.jumlah
            tempBucket[index].foto = this.state.foto
        }
        this.setState({ Bucket: tempBucket })
        // menutup komponen modal_Bucket
        $("#modal_Bucket").hide()
    }
    Drop = (item) => {
        // beri konfirmasi untuk menghapus data
        if (window.confirm("Apakah anda yakin ingin menghapus data ini?")) {
            // menghapus data
            let tempBucket = this.state.Bucket
            // posisi index data yg akan dihapus
            let index = tempBucket.indexOf(item)
            // hapus data
            tempBucket.splice(index, 1)
            this.setState({ Bucket: tempBucket })
        }
    }
}

export default Bucket;